mapper.select:
SELECT CHAT_ID 
  FROM USER_INFO
 WHERE CHAT_ID = #{chatId}

SELECT A.ITEM_PRICE
  FROM WISH_LIST A
 WHERE A.CHAT_ID = #{chatId}
     AND A.ITEM_NAME = #{itemName}

	SELECT COUNT(*)
	  FROM WISH_LIST A
     WHERE A.CHAT_ID = #{chatId}

	SELECT A.SEQ, A.CHAT_ID, A.ITEM_NAME, A.ITEM_PRICE, A.URL
	  FROM WISH_LIST A
    WHERE A.CHAT_ID = #{chatId}

mapper.update:
   UPDATE WISH_LIST
	    SET ITEM_PRICE = #{itemPrice}
	WHERE CHAT_ID = #{chatId}
	    AND SEQ = #{seq}
